// Education.js
import React, { useState } from 'react';
import { CSSTransition } from 'react-transition-group';
import 'animate.css/animate.min.css'; // Import animate.css for pre-built animations

const Education = () => {
  const slides = [
    { id: 1, university: 'University A', degree: 'Bachelor of Science', major: 'Computer Science', graduationYear: 2020 },
    { id: 2, university: 'University B', degree: 'Master of Arts', major: 'Graphic Design', graduationYear: 2022 },
    { id: 3, university: 'University C', degree: 'Bachelor of Business Administration', major: 'Marketing', graduationYear: 2018 },
    { id: 4, university: 'University D', degree: 'Master of Science', major: 'Data Science', graduationYear: 2021 },
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
  };

  return (
    <div className="container mx-auto mt-8 p-8 bg-gray-100 rounded-md">
      <h2 className="text-2xl font-bold mb-4">Education</h2>

      {/* Slideshow */}
      <div className="flex justify-between items-center">
        <button
          onClick={prevSlide}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          &lt; Prev
        </button>

        <CSSTransition
          in={true}
          appear={true}
          timeout={500}
          classNames="fade"
        >
          <div className="w-2/3 border border-gray-300 p-4 rounded-md bg-indigo-200">
            <h3 className="text-xl font-bold mb-2">{slides[currentSlide].university}</h3>
            <p>Degree: {slides[currentSlide].degree}</p>
            <p>Major: {slides[currentSlide].major}</p>
            <p>Graduation Year: {slides[currentSlide].graduationYear}</p>
          </div>
        </CSSTransition>

        <button
          onClick={nextSlide}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Next &gt;
        </button>
      </div>
    </div>
  );
};

export default Education;
